INSERT INTO `db_to_watch`.`media` (`id`, `type`, `title`, `release_date`, `nationality`, `synopsis`, `duration`, `average_duration`, `sessions_number`, `episodes_number`) VALUES (1, 'Film', 'Django desencadenado', '2013/01/18', 'Estados Unidos', 'Un antiguo esclavo une sus fuerzas con un cazador de recompensas aleman que lo libera y ayuda a cazar a los criminales mas buscados del Sur, todo ello con la esperanza de encontrar a su esposa perdida hace mucho tiempo.', 165, 0, 0, 0);
INSERT INTO `db_to_watch`.`media` (`id`, `type`, `title`, `release_date`, `nationality`, `synopsis`, `duration`, `average_duration`, `sessions_number`, `episodes_number`) VALUES (2, 'Film', 'Titanic', '1998/01/08', 'Estados Unidos', 'Jack es un joven artista que gana un pasaje para viajar a America en el Titanic, el transatlantico mas grande y seguro jamas construido.', 195, 0, 0, 0);
INSERT INTO `db_to_watch`.`media` (`id`, `type`, `title`, `release_date`, `nationality`, `synopsis`, `duration`, `average_duration`, `sessions_number`, `episodes_number`) VALUES (3, 'Film', 'Avatar', '2009/12/18', 'Estados Unidos', 'Entramos en el mundo Avatar de la mano de Jake Sully, un ex-Marine en silla de ruedas, que ha sido reclutado para viajar a Pandora, donde existe un mineral raro y muy preciado que puede solucionar la crisis energetica existente en la Tierra.', 162, 0, 0, 0);
INSERT INTO `db_to_watch`.`media` (`id`, `type`, `title`, `release_date`, `nationality`, `synopsis`, `duration`, `average_duration`, `sessions_number`, `episodes_number`) VALUES (4, 'Serie', 'La Casa del Dragon', '2022/08/21', 'Estados Unidos', 'La historia de la familia Targaryen 200 anios antes de los eventos que tuvieron lugar en Juego de tronos.', 0, 60, 1, 10);
INSERT INTO `db_to_watch`.`media` (`id`, `type`, `title`, `release_date`, `nationality`, `synopsis`, `duration`, `average_duration`, `sessions_number`, `episodes_number`) VALUES (5, 'Serie', 'Los Anillos de Poder', '2022/09/01', 'Estados Unidos', 'Los heroes se enfrentan al temido resurgimiento del mal en la Tierra Media, forjando legados que perduraran mucho tiempo despues de su desaparicion.', 0, 65, 1, 8);
INSERT INTO `db_to_watch`.`media` (`id`, `type`, `title`, `release_date`, `nationality`, `synopsis`, `duration`, `average_duration`, `sessions_number`, `episodes_number`) VALUES (6, 'Serie', 'The Last of Us', '2023/01/15', 'Estados Unidos', 'Joel y Ellie, dos personas conectadas por la brutalidad del mundo en el que viven, se ven obligados a enfrentarse a criaturas y asesinos despiadados, mientras viajan a traves de un Estados Unidos posapocaliptico.', 0, 50, 1, 9);
INSERT INTO `db_to_watch`.`media` (`id`, `type`, `title`, `release_date`, `nationality`, `synopsis`, `duration`, `average_duration`, `sessions_number`, `episodes_number`) VALUES (7, 'Serie', 'The Wire', '2002/06/02', 'Estados Unidos', 'En los barrios bajos de Baltimore, se investiga un asesinato relacionado con el mundo de las drogas. Un policía es el encargado de detener a los miembros de un importante cártel.', 0, 60, 5, 60);
INSERT INTO `db_to_watch`.`media` (`id`, `type`, `title`, `release_date`, `nationality`, `synopsis`, `duration`, `average_duration`, `sessions_number`, `episodes_number`) VALUES (8, 'Serie', 'Breaking Bad', '2008/01/20', 'Estados Unidos', 'Tras cumplir 50 años, Walter White, un profesor de química de un instituto de Albuquerque, Nuevo México, se entera de que tiene un cáncer de pulmón incurable.', 0, 45, 5, 62);
INSERT INTO `db_to_watch`.`media` (`id`, `type`, `title`, `release_date`, `nationality`, `synopsis`, `duration`, `average_duration`, `sessions_number`, `episodes_number`) VALUES (9, 'Serie', 'Juego de Tronos', '2011/04/17', 'Estados Unidos', 'Un mundo ficticio de carácter medieval donde hay Siete Reinos y sigue tres líneas argumentales: la guerra civil entre varias familias nobles; la amenaza de seres que viven al otro lado del muro; y el viaje de Daenerys Targaryen.', 0, 55, 8, 73);
INSERT INTO `db_to_watch`.`media` (`id`, `type`, `title`, `release_date`, `nationality`, `synopsis`, `duration`, `average_duration`, `sessions_number`, `episodes_number`) VALUES (10, 'Serie', 'Los Soprano', '1999/01/10', 'Estados Unidos', 'Crónica de la vida cotidiana y de las aventuras personales y profesionales de una familia mafiosa que vive en Nueva Jersey. Son gentes sencillas, pero implacables en sus ritos y tradiciones.', 0, 50, 6, 86);
INSERT INTO `db_to_watch`.`media` (`id`, `type`, `title`, `release_date`, `nationality`, `synopsis`, `duration`, `average_duration`, `sessions_number`, `episodes_number`) VALUES (11, 'Serie', 'Rick y Morty', '2013/12/02', 'Estados Unidos', 'Comedia animada que narra las aventuras de un científico loco, Rick Sánchez, que regresa después de 20 años para vivir con su hija, su marido y sus hijos, Morty y Summer.', 0, 22, 6, 61);
INSERT INTO `db_to_watch`.`media` (`id`, `type`, `title`, `release_date`, `nationality`, `synopsis`, `duration`, `average_duration`, `sessions_number`, `episodes_number`) VALUES (12, 'Serie', 'Romanzo criminale', '2008/11/10', 'Italia', 'Adaptación de la popular novela Romanzo criminale. Narra el ascenso y caída de la banda de la Magliana, integrada por un grupo de jóvenes delincuentes que dominaron el narcotráfico en Roma a finales de los 70.', 0, 55, 2, 22);
INSERT INTO `db_to_watch`.`media` (`id`, `type`, `title`, `release_date`, `nationality`, `synopsis`, `duration`, `average_duration`, `sessions_number`, `episodes_number`) VALUES (13, 'Serie', 'Arcane', '2021/11/06', 'Estados Unidos', 'Arcane ahonda en el precario equilibrio entre la rica ciudad de Piltover y la sórdida y oprimida Zaun. La tensión entre ambas ciudades se exacerba debido a la creación en Piltover de algo llamado "Hextech", y a una nueva droga llamada "Shimmer".', 0, 40, 1, 9);
INSERT INTO `db_to_watch`.`media` (`id`, `type`, `title`, `release_date`, `nationality`, `synopsis`, `duration`, `average_duration`, `sessions_number`, `episodes_number`) VALUES (14, 'Serie', 'Black Mirror', '2011/12/04', 'Reino Unido', 'Serie antológica creada por Charlie Brooker ("Dead Set") que muestra el lado oscuro de la tecnología y cómo esta afecta y puede alterar nuestra vida, a veces con consecuencias tan impredecibles como aterradoras.', 0, 60, 5, 21);
INSERT INTO `db_to_watch`.`media` (`id`, `type`, `title`, `release_date`, `nationality`, `synopsis`, `duration`, `average_duration`, `sessions_number`, `episodes_number`) VALUES (15, 'Serie', 'Friends', '1994/09/22', 'Estados Unidos', '"Friends" narra las aventuras y desventuras de seis jóvenes de Nueva York. Rachel, Monica, Phoebe, Ross, Chandler y Joey forman una unida pandilla de amigos que viven en Manhattan y que suelen reunirse en sus apartamentos o en su bar habitual.', 0, 20, 10, 236);
INSERT INTO `db_to_watch`.`media` (`id`, `type`, `title`, `release_date`, `nationality`, `synopsis`, `duration`, `average_duration`, `sessions_number`, `episodes_number`) VALUES (16, 'Serie', 'Death Note', '2006/10/03', 'Japón', ' Death Note cuenta la historia sobre un joven llamado Yagami Light. Un aburrido día de clase ve caer un cuaderno negro en el patio de su escuela, titulado Death Note. Aquella persona cuyo nombre sea escrito en dicho cuaderno, morirá.', 0, 25, 1, 37);
INSERT INTO `db_to_watch`.`media` (`id`, `type`, `title`, `release_date`, `nationality`, `synopsis`, `duration`, `average_duration`, `sessions_number`, `episodes_number`) VALUES (17, 'Serie', 'Peaky Blinders', '2013/09/12', 'Reino Unido', 'Una familia de gánsters asentada en Birmingham tras la Primera Guerra Mundial, dirige un local de apuestas hípicas. Las actividades de la banda llaman la atención del Inspector jefe Chester Campbell, que es enviado para limpiar la ciudad.', 0, 60, 6, 36);
INSERT INTO `db_to_watch`.`media` (`id`, `type`, `title`, `release_date`, `nationality`, `synopsis`, `duration`, `average_duration`, `sessions_number`, `episodes_number`) VALUES (18, 'Serie', 'The Office', '2005/03/24', 'Estados Unidos', 'Corrosivo retrato de los miembros de una empresa. Versión americana de una popular serie británica de la BBC del mismo título.', 0, 24, 9, 188);
INSERT INTO `db_to_watch`.`media` (`id`, `type`, `title`, `release_date`, `nationality`, `synopsis`, `duration`, `average_duration`, `sessions_number`, `episodes_number`) VALUES (19, 'Film', 'Ciudad de Dios', '2003/01/31', 'Brasil', 'Basada en hechos reales, describe el mundo del crimen organizado en Cidade de Deus, suburbio de Río de Janeiro, desde finales de los sesenta hasta principios de los ochenta, en el que el tráfico de drogas y la violencia impusieron su ley en las favelas.', 130, 0, 0, 0);
INSERT INTO `db_to_watch`.`media` (`id`, `type`, `title`, `release_date`, `nationality`, `synopsis`, `duration`, `average_duration`, `sessions_number`, `episodes_number`) VALUES (20, 'Film', 'El Pianista', '2002/11/15', 'Reino Unido', 'Wladyslaw Szpilman, un brillante pianista polaco de origen judío, vive con su familia en el ghetto de Varsovia. Cuando, en 1939, los alemanes invaden Polonia, consigue evitar la deportación gracias a la ayuda de algunos amigos.', 148, 0, 0, 0);
INSERT INTO `db_to_watch`.`media` (`id`, `type`, `title`, `release_date`, `nationality`, `synopsis`, `duration`, `average_duration`, `sessions_number`, `episodes_number`) VALUES (21, 'Film', 'Gran Torino', '2009/03/06', 'Estados Unidos', 'Walt Kowalski, un veterano de la guerra de Corea (1950-1953), es un obrero jubilado del sector del automóvil que ha enviudado recientemente. Su máxima pasión es cuidar de su más preciado tesoro: un coche Gran Torino de 1972. ', 119, 0, 0, 0);
INSERT INTO `db_to_watch`.`media` (`id`, `type`, `title`, `release_date`, `nationality`, `synopsis`, `duration`, `average_duration`, `sessions_number`, `episodes_number`) VALUES (22, 'Film', 'El Señor de los Anillos: El Retorno del Rey', '2003/12/17', 'Nueva Zelanda', 'Las fuerzas de Saruman han sido destruidas, y su fortaleza sitiada. Ha llegado el momento de decidir el destino de la Tierra Media, y, por primera vez, parece que hay una pequeña esperanza.', 201, 0, 0, 0);
INSERT INTO `db_to_watch`.`media` (`id`, `type`, `title`, `release_date`, `nationality`, `synopsis`, `duration`, `average_duration`, `sessions_number`, `episodes_number`) VALUES (23, 'Film', 'El caballero oscuro', '2008/08/13', 'Estados Unidos', 'Batman/Bruce Wayne regresa para continuar su guerra contra el crimen. Con la ayuda del teniente Jim Gordon y del Fiscal del Distrito Harvey Dent, Batman se propone destruir el crimen organizado en la ciudad de Gotham.', 152, 0, 0, 0);
INSERT INTO `db_to_watch`.`media` (`id`, `type`, `title`, `release_date`, `nationality`, `synopsis`, `duration`, `average_duration`, `sessions_number`, `episodes_number`) VALUES (24, 'Film', 'El secreto de sus ojos', '2009/09/25', 'Argentina', 'Benjamín Espósito es oficial de un Juzgado de Instrucción de Buenos Aires recién retirado. Obsesionado por un brutal asesinato ocurrido veinticinco años antes, en 1974, decide escribir una novela sobre el caso, del cual fue testigo y protagonista.', 129, 0, 0, 0);
INSERT INTO `db_to_watch`.`media` (`id`, `type`, `title`, `release_date`, `nationality`, `synopsis`, `duration`, `average_duration`, `sessions_number`, `episodes_number`) VALUES (25, 'Film', 'El viaje de Chihiro', '2002/10/25', 'Japón', 'Chihiro es una niña de diez años que viaja en coche con sus padres. Después de atravesar un túnel, llegan a un mundo fantástico, en el que no hay lugar para los seres humanos, sólo para los dioses de primera y segunda clase.', 124, 0, 0, 0);
INSERT INTO `db_to_watch`.`media` (`id`, `type`, `title`, `release_date`, `nationality`, `synopsis`, `duration`, `average_duration`, `sessions_number`, `episodes_number`) VALUES (26, 'Film', 'Million Dollar Baby', '2005/02/04', 'Estados Unidos', 'Después de haber entrenado y representado a los mejores, Frankie Dunn regenta un gimnasio con la ayuda de Scrap, su único amigo. Un día, entra en su gimnasio Maggie, una chica que quiere boxear y está dispuesta a luchar para conseguirlo.', 132, 0, 0, 0);
INSERT INTO `db_to_watch`.`media` (`id`, `type`, `title`, `release_date`, `nationality`, `synopsis`, `duration`, `average_duration`, `sessions_number`, `episodes_number`) VALUES (27, 'Film', 'Intocable', '2012/03/09', 'Francia', 'Philippe, un aristócrata millonario que se ha quedado tetrapléjico a causa de un accidente de parapente, contrata como cuidador a domicilio a Driss, un inmigrante de un barrio marginal recién salido de la cárcel.', 109, 0, 0, 0);
INSERT INTO `db_to_watch`.`media` (`id`, `type`, `title`, `release_date`, `nationality`, `synopsis`, `duration`, `average_duration`, `sessions_number`, `episodes_number`) VALUES (28, 'Film', 'Coco', '2017/11/22', 'Estados Unidos', 'Miguel es un joven con el sueño de convertirse en leyenda de la música a pesar de la prohibición de su familia. Su ídolo es Ernesto de la Cruz, el músico y cantante más famoso de México.', 109, 0, 0, 0);
INSERT INTO `db_to_watch`.`media` (`id`, `type`, `title`, `release_date`, `nationality`, `synopsis`, `duration`, `average_duration`, `sessions_number`, `episodes_number`) VALUES (29, 'Film', 'Joker', '2019/10/04', 'Estados Unidos', 'Arthur Fleck vive en Gotham con su madre, y su única motivación en la vida es hacer reír a la gente. Actúa haciendo de payaso en pequeños trabajos, pero tiene problemas mentales que hacen que la gente le vea como un bicho raro.', 121, 0, 0, 0);
INSERT INTO `db_to_watch`.`media` (`id`, `type`, `title`, `release_date`, `nationality`, `synopsis`, `duration`, `average_duration`, `sessions_number`, `episodes_number`) VALUES (30, 'Film', 'Origen', '2010/08/06', 'Estados Unidos', 'Dom Cobb es un experto en el arte de apropiarse, durante el sueño, de los secretos del subconsciente ajeno. Esta habilidad le ha convertido en un hombre muy cotizado, pero también lo ha condenado a ser un fugitivo y a renunciar a vida normal.', 148, 0, 0, 0);
